package practice.accessmodifiers;

public class loops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//*
		//**
		//***
		//****
for (int i=0; i<10; i++) {
	for (int j=0; j<=i; j++) {
		System.out.print("x");
}
	System.out.print("\n");
	}
	



////    *
//     **
//    ***
//   ****
for (int i=0; i<10; --i) {
for (int j=0; j<=i; --j) {
System.out.print("x");
}
System.out.print("\n");
}
}
}